CREATE TABLE IF NOT EXISTS `organization` (
    `id` INT(11) NOT NULL AUTO_INCREMENT,
    `organization_id` INT(11) NOT NULL DEFAULT '0',
    `actual_from` DATE NOT NULL DEFAULT '2000-01-01',
    `actual_to` DATE NOT NULL DEFAULT '4000-01-01',
    `firma` VARCHAR(128) NULL DEFAULT NULL COMMENT 'Ключ для связи с clients',
    `country_id` INT(4) NOT NULL DEFAULT '643',
    `lang_code` VARCHAR(5) NOT NULL DEFAULT 'ru',
    `tax_system` ENUM('ОСНО','УСН') NOT NULL DEFAULT 'ОСНО',
    `vat_rate` SMALLINT(6) NOT NULL DEFAULT '0',
    `name` VARCHAR(250) NOT NULL,
    `full_name` VARCHAR(250) NULL DEFAULT NULL,
    `legal_address` VARCHAR(250) NULL DEFAULT NULL,
    `post_address` VARCHAR(250) NULL DEFAULT NULL,
    `registration_id` VARCHAR(250) NULL DEFAULT NULL,
    `tax_registration_id` VARCHAR(32) NULL DEFAULT NULL,
    `tax_registration_reason` VARCHAR(12) NULL DEFAULT NULL,
    `bank_account` VARCHAR(128) NULL DEFAULT NULL,
    `bank_name` VARCHAR(255) NULL DEFAULT NULL,
    `bank_correspondent_account` VARCHAR(64) NULL DEFAULT NULL,
    `bank_bik` VARCHAR(20) NULL DEFAULT NULL,
    `bank_swift` VARCHAR(11) NULL DEFAULT NULL,
    `contact_phone` VARCHAR(148) NULL DEFAULT NULL,
    `contact_fax` VARCHAR(148) NULL DEFAULT NULL,
    `contact_email` VARCHAR(128) NULL DEFAULT NULL,
    `contact_site` VARCHAR(250) NULL DEFAULT NULL,
    `logo_file_name` VARCHAR(50) NULL DEFAULT NULL,
    `stamp_file_name` VARCHAR(50) NULL DEFAULT NULL,
    `director_id` INT(11) NULL DEFAULT NULL,
    `accountant_id` INT(11) NULL DEFAULT NULL,
    PRIMARY KEY (`id`),
    INDEX `organization_id_actual_from` (`organization_id`, `actual_from`),
    INDEX `fk_organization__director_id` (`director_id`),
    INDEX `fk_organization__accountant_id` (`accountant_id`),
    CONSTRAINT `fk_organization__director_id` FOREIGN KEY (`director_id`) REFERENCES `person` (`id`)ON DELETE RESTRICT ON UPDATE CASCADE,
    CONSTRAINT `fk_organization__accountant_id` FOREIGN KEY (`accountant_id`) REFERENCES `person` (`id`) ON DELETE RESTRICT ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

